import { useDispatch, useSelector } from "react-redux";
import { setRestaurants } from "../components/Slicers/restaurantsSlicer";
import FetchAllData from "./FetchAllData";

export async function LoadAllData(){
    const dispatch = useDispatch();
    const AllRestaurants = await FetchAllData('http://localhost:3001/api/restaurants/getRestaurants');
    dispatch(setRestaurants(AllRestaurants));
    const ress = useSelector((state:any) => state.restaurants.value);
    console.log(ress);
}
