import React, { useEffect, useState } from 'react'
import { Allrestaurants, Restaurant } from '../../../interfaces/Restaurant'
import { RestaurantDiv, RestaurantImg, RestaurantName } from '../ChefOfTheWeek/StyleChefDetails'
import { RestaurantStatus, RestChefName, StatusSelector, TitleR } from './StylePopularRests'

export default function AllRestaurants() {
  const [filteredRestaurants, setFilteredRestaurants] = useState<Restaurant[]>([]);
  const [isActive, setIsActive] = useState(false);
  // function filterRestaurants(status:string){
  //   const FilteredRests =Allrestaurants.filter(restaurant => restaurant.Status===status)
  // return FilteredRests; }
  // const FilteredRests = (status:string) => {Allrestaurants.filter(restaurant => restaurant.Status === status) }
  const filterRests = (status: string) => {
    const filtered = Allrestaurants.filter(restaurant => restaurant.Status.includes(status));
    setFilteredRestaurants(filtered);
    setIsActive(current => !current);
  }


  useEffect(() => {
    filterRests("All");
      }, []);

  return (
    <div>

        <TitleR> RESTAURANTS </TitleR>

        <StatusSelector>
            <RestaurantStatus onClick={() => filterRests("All")}> All </RestaurantStatus>
            <RestaurantStatus onClick={() => filterRests("New")}> New </RestaurantStatus>
            <RestaurantStatus onClick={() => filterRests("Most Popular")}> Most Popular </RestaurantStatus>
            <RestaurantStatus onClick={() => filterRests("Open now")}> Open Now </RestaurantStatus>
        </StatusSelector>


        {filteredRestaurants.map((restaurant:Restaurant)=>(
                  <RestaurantDiv> 
                      <RestaurantImg src={restaurant.PictureURL} alt=''/>
                      <RestaurantName> {restaurant.name}</RestaurantName>
                      <RestChefName>{restaurant.ChefName}</RestChefName>
                  </RestaurantDiv>))}
    </div>
  )
}
