import styled from "styled-components";
//import imggg from "assets/hero-picture.png";
const imgg = "./assets/hero-picture.png";
export const HeroP = styled.div`
    backgroundImage: url(${imgg});
    width: 69.5%;
    height: 100%;
    padding: 15%;`

export const HeroParagraph = styled.p`
    color: black;
    font-family: 'Courier New', Courier, monospace;
    background-color: rgba(255, 255, 255, 0.6);
    text-align: center;
    font-size: larger;
    padding: 7%;`

export const Searchbar = styled.div`margin-top: 5%;`

export const SearchBox = styled.input`
                                width: 80%;
                                background-color: transparent;
                                border: solid;
                                border-radius: 10%;
                                ::placeholder {
                                    color: palevioletred;
                                }
                                `;

export const SearchButton = styled.button` background-color: #4CAF50;
border: none;
color: white;
padding: 15px 32px;
text-align: center;
text-decoration: none;
display: inline-block;
font-size: 16px;`

// input & img (fix background)
