import React, { Component } from 'react'
import { chefOfTheWeek } from '../../../interfaces/Chef'
import './temp.css'
import Slider from "react-slick";
import { TitlePP } from './StyleChefDetails';
export default class ChefRestaurants extends Component  {
/*  return (
    <div className='rests'>
        {chefOfTheWeek.restaurants.map((restaurant)=>(
           <div> 
            <p><img src={restaurant.resImgUrl} alt=''/> </p>
            <p> {restaurant.name}</p>
            </div>))}

    </div>
  )
}*/
render() {
    const settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 2,
      slidesToScroll: 1
    };
    return (
      <div id="CarouselID">
        <TitlePP> Restaurants Of the Chef: </TitlePP>
        <Slider {...settings}>
        {chefOfTheWeek.restaurants.map((restaurant)=>(
           <div className='restaurant'> 
            <img src={restaurant.resImgUrl} alt=''/>
            <p> {restaurant.name}</p>
            </div>))}
        </Slider>
        <br/>
        <TitlePP>All Restaurants &gt;&gt;</TitlePP>
      </div>
    );
  }
}
