import React from 'react'
import { useSelector } from 'react-redux';
import { setRestaurants } from '../components/Slicers/restaurantsSlicer';
import store from '../store';

export default async function FetchAllData(apiName:string,MethodName:string) {
        const result = await fetch('http://localhost:3001/api/'+{apiName}+'/'+{MethodName});
        const jsonData = await result.json();
        store.dispatch(setRestaurants(jsonData));
        const ress = useSelector((state:any) => state.restaurants.value);
        console.log(ress);
}
FetchAllData('restaurants','getRestaurants');
