import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'

export const restaurantsSlice = createSlice({
  name: 'restaurants',
  initialState: {
    value: []
  },
  reducers: {
    setRestaurants: (state, action) => {
      state.value = action.payload;
    },
    getRestaurants: (state, action) => {
      const fetchData = async () =>{
           const result = await fetch('http://localhost:3001/api/restaurants/getRestaurants');
           console.log(result)
          }
    fetchData();
  }
  },
})

// Action creators are generated for each case reducer function
export const { setRestaurants } = restaurantsSlice.actions
export const { getRestaurants } = restaurantsSlice.actions


export default restaurantsSlice.reducer
