import styled from "styled-components";
import { chefOfTheWeek } from "../../../interfaces/Chef";

export const TitleC = styled.p` justify-content: start;
display: flex;
margin-left:7.5% ;
font-family: 'Courier New', Courier, monospace;
color: rgb(100, 100, 100);`

export const PicName = styled.div`  width:85% ;
height: 60%;
display: inline-block;`

const url = chefOfTheWeek.PictureURL;
export const ChefPic = styled.img``
ChefPic.defaultProps = {
    src: url,
  };

  export const ChefName = styled.p` background-color: rgb(215, 213, 213);
  width: 100%;
  display: inline-block;
  margin-top: 0px;
  font-family: 'Courier New', Courier, monospace;`

  export const ChefDescriptin = styled.p` margin-top: 0%;
  margin-left: 7.5%;
  margin-right: 7.5%;
  font-family: 'Courier New', Courier, monospace;`
