import React, { Component } from 'react'
import Slider from 'react-slick';
import { Allrestaurants } from '../../../interfaces/Restaurant';
import { CarouselDiv, RestaurantDiv, RestaurantImg, RestaurantName, TitlePP } from '../ChefOfTheWeek/StyleChefDetails';
import { RestChefName } from '../PopularRestaurants/StylePopularRests';
import BtnAllDishes from './BtnAllDishes';

    export default class SignaturesDish extends Component  {

        render() {
            const settings = {
              dots: true,
              infinite: false,
              speed: 500,
              slidesToShow: 2,
              slidesToScroll: 1
            };
            return (
              <CarouselDiv>
                <TitlePP> Signature Dish Of:: </TitlePP>
                <Slider {...settings}>
                {Allrestaurants.slice(0,4).map((restaurant)=>(
                   <RestaurantDiv> 
                    <RestaurantImg src={restaurant.PictureURL} alt=''/>
                    <RestaurantName> {restaurant.name}</RestaurantName>
                    <RestChefName>{restaurant.ChefName}</RestChefName>
                    </RestaurantDiv>))}
                </Slider>
                <br/>
                <BtnAllDishes/>
              </CarouselDiv>

            );
          }
        }
