import styled from "styled-components";

export const TitleR = styled.p `justify-content: start;
display: flex;
margin-left:7.5% ;
font-family: Helvetica Neue;
color: rgb(100, 100, 100);
font-size:larger`;

export const AllRestsBtn = styled.button` background:none;
                            border:none;
                            margin:0;
                            width:100%;
                            height:10%;
                            cursor: pointer;
                            display: flex;
                            justify-content: start;
                            font-weight:bold;`

export const RestChefName = styled.p`justify-content: start;
display: flex;
font-family: Helvetica Neue;
color: rgb(100, 100, 100);
font-size:medium;
margin-left: 7.5%;`

export const StatusSelector =styled.div`
display:flex;
justify-content: space-between;
margin-left : 5%;
margin-right:5%;
margin-bottom:5%;
`

export const RestaurantStatus = styled.button`
font-family:Helvetica Neue;
font-size:18px;
font-weight : ${({ allBtnisActive }) => allBtnisActive ? 200:400};
line-height: 22px;
background:none;
border:none;
margin:0;
width:fit-content;
height:10%;
cursor: pointer;


`
