import styled from "styled-components";

export const Container = styled.div` background-color:#FAFAFA;
                                      width: 100%;
                                      height: 584px;
                                      display: flex;
                                      flex-direction: column;
                                      align-items: center;
                                      justify-content: space-around;
                                      margin-bottom:10%;`

export const TypeDiv = styled.div` width:100%;
                                    height:33%; `

export const DishType = styled.p`
  font-family: Helvetica Neue;
  font-size:18px;
  font-weight:200;
  line-height: 30px;
                                `

export const SpicyLogo = styled.img`
        width: 30%;
        height: 30%;
                    `
    SpicyLogo.defaultProps = {
    src: "./assets/Spicy.svg",
  };

  export const VegitarianLogo = styled.img`
        width: 30%;
        height: 30%;
    `
    VegitarianLogo.defaultProps = {
    src: "./assets/Vegitarian.svg",
  };

  export const VeganLogo = styled.img`
          width: 30%;
          height: 30%;
    `
    VeganLogo.defaultProps = {
    src: "./assets/Vegan.svg",
  };
