  const[SignUpOpen,setSignUpOpen] = useState(true);
  const toggleSignUp = () => {
    setSignUpOpen(!SignUpOpen);
    }
  return (
    <div>
        <HeaderHamOpened>
            <img onClick={toggleSignUp} src='./assets/X.svg' alt=''/>
        </HeaderHamOpened>
        {SignUpOpen &&(<SignUp/>)}
    </div>
  )
}
