    height: 100%;
    padding: 15%;
    margin-bottom:10%;
`

export const Herotext =  styled.p`
                                    color: black;
                                    font-family: 'Courier New', Courier, monospace;
                                    background-color: rgba(255, 255, 255, 0.8);
                                    text-align: center;
                                    font-size: larger;
                                    display:inline-block;
