import React, { useEffect, useState } from 'react'
import { Allrestaurants, Restaurant } from '../../../interfaces/Restaurant';
import { RestaurantDiv, RestaurantImg, RestaurantName } from '../ChefOfTheWeek/StyleChefDetails';
import { DesktopAllRestsContainer, DesktopRestauranDiv, DownArrow, FilterOptionButton, FilterOptions, RestaurantStatus, RestChefName, StarsImg, StatusSelector, TitleR } from './StylePopularRests';
import { Wrapper, Status } from "@googlemaps/react-wrapper";
import { RestaurantWordHeader } from '../../Header/StyleHeader';
export default function DesktopAllRestaurants() {
    const [filteredRestaurants, setFilteredRestaurants] = useState<Restaurant[]>([]);
  const [allBtnisActive, setAllIsActive] = useState(true);
  const [newBtnisActive, setNewIsActive] = useState(false);
  const [popularBtnisActive, setPopularIsActive] = useState(false);
  const [openBtnisActive, setOpenIsActive] = useState(false);
  const [MapBtnisActive, setMapIsActive] = useState(false);

  const filterRests = (status: string) => {
    const filtered = Allrestaurants.filter(restaurant => restaurant.Status.includes(status));
    setFilteredRestaurants(filtered);
  }

  function MakeAllActive(){
    setAllIsActive(true);
    setNewIsActive(false);
    setPopularIsActive(false);
    setOpenIsActive(false);
    setMapIsActive(false);

  }

  function MakeNewActive(){
    setAllIsActive(false);
    setNewIsActive(true);
    setPopularIsActive(false);
    setOpenIsActive(false);
    setMapIsActive(false);
  }

  function MakePopularActive(){
    setAllIsActive(false);
    setNewIsActive(false);
    setPopularIsActive(true);
    setOpenIsActive(false);
    setMapIsActive(false);
  }

  function MakeOpenActive(){
    setAllIsActive(false);
    setNewIsActive(false);
    setPopularIsActive(false);
    setOpenIsActive(true);
    setMapIsActive(false);
  }
  function MapViewActive(){
    setAllIsActive(false);
    setNewIsActive(false);
    setPopularIsActive(false);
    setOpenIsActive(false);
    setMapIsActive(true);
  }


  useEffect(() => {
    filterRests("All");
    setAllIsActive(true);
  }, []);

//   const ref = React.useRef<HTMLDivElement>(null);
//   const [map, setMap] = React.useState<google.maps.Map>();

//   React.useEffect(() => {
//     if (ref.current && !map) {
//       setMap(new window.google.maps.Map(ref.current, {}));
//     }
//   }, [ref, map]);

const [ price, setPrice ] = useState(40);

  // Triggered when the value gets updated while scrolling the slider:
  const handleInput = (e:any)=>{
    setPrice( e.target.value );
  }
  const hotels = [
    { name: "A", price: 2  },
    { name: "B", price: 3  },
    { name: "C", price: 4  }
  ];





  return (
    <div>

        <StatusSelector Mobile={false}>
            <RestaurantStatus underline={allBtnisActive} onClick={() =>{filterRests("All");
             MakeAllActive() }}> All </RestaurantStatus>

            <RestaurantStatus underline={newBtnisActive}  onClick={() => {filterRests("New");
            MakeNewActive()} }> New </RestaurantStatus>

            <RestaurantStatus underline={popularBtnisActive} onClick={() => {filterRests("Most Popular");MakePopularActive()}}> Most Popular </RestaurantStatus>

            <RestaurantStatus underline={openBtnisActive}  onClick={() => {filterRests("Open now"); MakeOpenActive()}}> Open Now </RestaurantStatus>

            <RestaurantStatus underline={MapBtnisActive}  onClick={() => {filterRests("Map View"); MapViewActive()}}> Map View </RestaurantStatus>
        </StatusSelector>
        <FilterOptions>
            <FilterOptionButton> Price Range <DownArrow/> </FilterOptionButton>
            <FilterOptionButton> Distance <DownArrow/> </FilterOptionButton>
            <FilterOptionButton> Rating <DownArrow/></FilterOptionButton>
        </FilterOptions>

        <div className="App">
            <input type="range" onInput={ handleInput } />
            <h1>Price: { price }</h1>
      <div>
      { filteredRestaurants.filter( rest => { return rest.dishes.length >= price }).map( rest => {
        return <DesktopRestauranDiv > 
        <RestaurantImg src={rest.PictureURL} alt=''/>
        <RestaurantName Mobile={false}> {rest.name}</RestaurantName>
        <RestChefName Mobile={false}>{rest.ChefName}</RestChefName>
        <StarsImg src={rest.stars} alt=''/>
        </DesktopRestauranDiv>
      })}        
      </div>
        <DesktopAllRestsContainer>
        {filteredRestaurants.map((restaurant:Restaurant , index)=>(
                  <DesktopRestauranDiv key={index} > 
                      <RestaurantImg src={restaurant.PictureURL} alt=''/>
                      <RestaurantName Mobile={false}> {restaurant.name}</RestaurantName>
                      <RestChefName Mobile={false}>{restaurant.ChefName}</RestChefName>
                      <StarsImg src={restaurant.stars} alt=''/>
                  </DesktopRestauranDiv>))}
        </DesktopAllRestsContainer>

    </div>
    </div>
  )
}
