import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { SearchBar } from '../Body/HeroHome/HeroStyle'
import { AllRestsBtn } from '../Body/PopularRestaurants/StylePopularRests'
import MobileHomePage from '../HomePage/MobileHomePage'
import { SearchDiv } from '../HomePage/StyleHomePage'
import Header from './Header'
import { SearchHeader, SearchWordHeader, Ximg } from './StyleHeader'

export default function OpenedSearch() {
    const[SearchClose,setSearchClose] = useState(true);
    const CloseSearch = () => {
        setSearchClose(false);
    }
  return (
    <div>
           <SearchDiv>
           <SearchHeader>
                <AllRestsBtn onClick={CloseSearch}> <Ximg src='./assets/x.svg' alt=''/> </AllRestsBtn>
                <SearchWordHeader> Search </SearchWordHeader>
            </SearchHeader>
                <SearchBar placeholder=" &#xF002; Search For restaurant cuisine, chef"/>

        </SearchDiv>
        {!SearchClose&&(<Header/>)}
    </div>
  )
}
