import React, { Component } from 'react'
import { chefOfTheWeek } from '../../../interfaces/Chef'
import './temp.css'
import Slider from "react-slick";
import { CarouselDiv, RestaurantImg, TitlePP } from './StyleChefDetails';
import BtnChefAllRests from './BtnChefAllRests';
export default class ChefRestaurants extends Component  {

render() {
    const settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 2,
      slidesToScroll: 1
    };
    return (
      <CarouselDiv>
        <TitlePP> Restaurants Of the Chef: </TitlePP>
        <Slider {...settings}>
        {chefOfTheWeek.restaurants.slice(0,3).map((restaurant)=>(
           <div className='restaurant'> 
            <RestaurantImg src={restaurant.resImgUrl} alt=''/>
            <p> {restaurant.name}</p>
            </div>))}
        </Slider>
        <br/>
        <BtnChefAllRests/>
      </CarouselDiv>
    );
  }
}
