import React, { Component } from 'react'
import { useNavigate } from 'react-router-dom';
import Slider from 'react-slick';
import { chefOfTheWeek } from '../../../interfaces/Chef';
import { Allrestaurants } from '../../../interfaces/Restaurant';
import { CarouselDiv, RestaurantDiv, RestaurantImg, RestaurantName, TitlePP } from '../ChefOfTheWeek/StyleChefDetails';
import BtnAllRests from './BtnAllRests';
import { RestChefName } from './StylePopularRests';



    export default class RestaurantsInEpicure extends Component  {

        render() {
            const settings = {
              dots: true,
              infinite: false,
              speed: 500,
              slidesToShow: 2,
              slidesToScroll: 1
            };
            return (
              <CarouselDiv>
                <TitlePP> POPULAR RESTAURANTS IN EPICURE: </TitlePP>
                <Slider {...settings}>
                {Allrestaurants.slice(0,4).map((restaurant)=>(
                   <RestaurantDiv> 
                    <RestaurantImg src={restaurant.PictureURL} alt=''/>
                    <RestaurantName> {restaurant.name}</RestaurantName>
                    <RestChefName>{restaurant.ChefName}</RestChefName>
                    </RestaurantDiv>))}
                </Slider>
                <br/>
                <BtnAllRests/>
              </CarouselDiv>
            );
          }
        }
