import React, { Component } from 'react'
import Slider from 'react-slick';
import { chefOfTheWeek } from '../../../interfaces/Chef';
import { Allrestaurants } from '../../../interfaces/Restaurant';
import { CarouselDiv, TitlePP } from '../ChefOfTheWeek/StyleChefDetails';

    export default class RestaurantsInEpicure extends Component  {
        /*  return (
            <div className='rests'>
                {chefOfTheWeek.restaurants.map((restaurant)=>(
                   <div> 
                    <p><img src={restaurant.resImgUrl} alt=''/> </p>
                    <p> {restaurant.name}</p>
                    </div>))}

            </div>
          )
        }*/
        render() {
            const settings = {
              dots: true,
              infinite: false,
              speed: 500,
              slidesToShow: 2,
              slidesToScroll: 1
            };
            return (
              <CarouselDiv>
                <TitlePP> POPULAR RESTAURANTS IN EPICURE: </TitlePP>
                <Slider {...settings}>
                {Allrestaurants.slice(0,4).map((restaurant)=>(
                   <div className='restaurant'> 
                    <img src={restaurant.PictureURL} alt=''/>
                    <p> {restaurant.name}</p>
                    <p>{restaurant.ChefName}</p>
                    </div>))}
                </Slider>
                <br/>
                <a href='src/components/Body/PopularRestaurants/AllRestaurants.tsx'>  <TitlePP>All Restaurants &gt;&gt;</TitlePP> </a>
              </CarouselDiv>
            );
          }
        }
