import axios from 'axios';
import React, { useState } from 'react'
import HeaderHamburger from './HeaderHamburger'
import { ButtonDiv, SignInButton, SignInDiv, SignInInput, SignInP } from './StyleHeader'

export default function SignUp() {
    const [Email, setEmail] = useState('');
    const [Password, setPassword] = useState('');
    const [Confirm, setConfirm] = useState('');

    const jsonData = {
        "Email":Email,
        "Password":Password
    }

  const handleChangeEmail = (event:any) => {
    setEmail(event.target.value);
  };
  const handleChangePassword = (event:any) => {
    setPassword(event.target.value);
  };
  const handleChangeConfirm = (event:any) => {
    setConfirm(event.target.value);
  };

   const AddUser = () => {
    // Send data to the backend via POST
    fetch('http://localhost:3001/api/users/createUser', {  // Enter your IP address here
      method: 'POST', 
      mode: 'cors',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(jsonData) // body data type must match "Content-Type" header
    })
    }
  return (
      <SignInDiv>
        <HeaderHamburger/>
        <SignInP> SIGN UP </SignInP>
        <SignInInput onChange={handleChangeEmail}
        value={Email} placeholder='Email address'/><br/>
        <SignInInput onChange={handleChangePassword} value={Password} placeholder='Password'/><br/>
        <SignInInput onChange={handleChangeConfirm} value={Confirm} placeholder='Confirm Password'/><br/>
        <ButtonDiv>
        <SignInButton onClick={AddUser}> SIGN UP </SignInButton>
        </ButtonDiv>
    </SignInDiv>
  )
}
