import React, { useEffect, useState } from 'react'
import { Allrestaurants, Restaurant } from '../../../interfaces/Restaurant'
import { RestaurantDiv, RestaurantImg, RestaurantName } from '../ChefOfTheWeek/StyleChefDetails'
import { RestaurantStatus, RestaurantStatusAll, RestaurantStatusNew, RestaurantStatusOpenNow, RestaurantStatusPopular, RestChefName, StatusSelector, TitleR } from './StylePopularRests'

export default function AllRestaurants() {
  const [underline, setUnderline] = useState(true);
  const [filteredRestaurants, setFilteredRestaurants] = useState<Restaurant[]>([]);
  const [allBtnisActive, setAllIsActive] = useState(true);
  const [newBtnisActive, setNewIsActive] = useState(false);
  const [popularBtnisActive, setPopularIsActive] = useState(false);
  const [openBtnisActive, setOpenIsActive] = useState(false);
  // function filterRestaurants(status:string){
  //   const FilteredRests =Allrestaurants.filter(restaurant => restaurant.Status===status)
  // return FilteredRests; }
  // const FilteredRests = (status:string) => {Allrestaurants.filter(restaurant => restaurant.Status === status) }
  const filterRests = (status: string) => {
    const filtered = Allrestaurants.filter(restaurant => restaurant.Status.includes(status));
    setFilteredRestaurants(filtered);
  }

  function MakeAllActive(){
    setAllIsActive(true);
    setNewIsActive(false);
    setPopularIsActive(false);
    setOpenIsActive(false);
  }

  function MakeNewActive(){
    setAllIsActive(false);
    setNewIsActive(true);
    setPopularIsActive(false);
    setOpenIsActive(false);
  }

  function MakePopularActive(){
    setAllIsActive(false);
    setNewIsActive(false);
    setPopularIsActive(true);
    setOpenIsActive(false);
  }

  function MakeOpenActive(){
    setAllIsActive(false);
    setNewIsActive(false);
    setPopularIsActive(false);
    setOpenIsActive(true);
  }


  useEffect(() => {
    filterRests("All");
    setAllIsActive(true);
  }, []);

  return (
    <div>

        <TitleR> RESTAURANTS </TitleR>

        <StatusSelector>
            <RestaurantStatusAll underline={allBtnisActive} onClick={() =>{filterRests("All");
             MakeAllActive() }}> All </RestaurantStatusAll>

            <RestaurantStatusNew underline={newBtnisActive}  onClick={() => {filterRests("New");
            MakeNewActive()} }> New </RestaurantStatusNew>

            <RestaurantStatusPopular underline={popularBtnisActive} onClick={() => {filterRests("Most Popular");MakePopularActive()}}> Most Popular </RestaurantStatusPopular>

            <RestaurantStatusOpenNow underline={openBtnisActive}  onClick={() => {filterRests("Open now"); MakeOpenActive()}}> Open Now </RestaurantStatusOpenNow>
        </StatusSelector>


        {filteredRestaurants.map((restaurant:Restaurant)=>(
                  <RestaurantDiv> 
                      <RestaurantImg src={restaurant.PictureURL} alt=''/>
                      <RestaurantName> {restaurant.name}</RestaurantName>
                      <RestChefName>{restaurant.ChefName}</RestChefName>
                  </RestaurantDiv>))}
    </div>
  )
}
<AllRestaurants/>
